<?php/** * Contains the data (breakfast/lunch/dinner) for a particular day */class Day{	private $_name;	private $_dt;    private $_breakfast;	private $_lunch;	private $_dinner;    public function __construct($name,$dt)    {        $this->_name = $name;        //$this->_dt = date('d/m/y', strtotime($dt));		$this->_dt = date('Y-m-d', strtotime($dt));		$this->_breakfast = "";		$this->_lunch = "";		$this->_dinner = "";				//$dt = date('Y-m-d', strtotime($dt));				//$this->_breakfast = $this->getMealName($dt, "B");		//$this->_lunch = $this->getMealName("$dt", "L");		//$this->_dinner = $this->getMealName("$dt", "E");	}		/**     * Returns name of meal based on date and meal     * (B breakfast, L lunch, E evening meal	 *     */     /*public function getMealName($date, $meal)    {		include_once "../db/functions.php";        $mysqli = iConnect("mealmanager");		$qdata = "SELECT l.mealid,l.meal,l.dt,m.title			FROM meallog l, meals m			WHERE l.dt='$date'			AND l.meal=$meal			AND l.mealid=m.id";		$data = $mysqli->query($qdata); 		$num_rows = $data->num_rows;		if ($num_rows > 0)		{			while ($row = $data->fetch_array())			{				$this->_title = $row['title'];			}		}		return $this->_title;    }*/		public function setMealData($Meal, $MealID)    {		if (is_null($MealID))		  $MealID == "";		  		switch($Meal)		{			case "B":				$this->_breakfast = $MealID;			break;			case "L":				$this->_lunch = $MealID;			break;			case "E":				$this->_dinner = $MealID;			break;		}		    }		public function setLunch($Lunch)    {		$this->_lunch = $Lunch;    }		public function setDinner($Dinner)    {		$this->_dinner = $Dinner;    }		public function getBreak()    {		return $this->_breakfast;    }		public function getLunch()    {		return $this->_lunch;    }		public function getDinner()    {		return $this->_dinner;    }		public function getDT()    {		return $this->_dt;    }}?>