<?phpinclude "../db/functions.php";//Check if user is legitimately logged insession_start();if (!isset($_SESSION["valid_user"])){	// User not logged in, redirect to login page	header("Location: index.php");	exit();}//$now=date("Y-m-d");$now=$_SESSION["start_date"];//$dayno=date("w",strtotime($now));$dayno=date("w",strtotime($_SESSION["start_date"]));switch($dayno){	case 0://sunday		$start = date('Y-m-d', strtotime($now. " -6 days"));		$end = date('Y-m-d', strtotime($now));	break;	case 1://monday		$start = date('Y-m-d', strtotime($now));		$end = date('Y-m-d', strtotime($now. " +6 days"));	break;	case 2://tuesday		$start = date('Y-m-d', strtotime($now. " -1 days"));		$end = date('Y-m-d', strtotime($now. " +5 days"));	break;	case 3://wednesday		$start = date('Y-m-d', strtotime($now. " -2 days"));		$end = date('Y-m-d', strtotime($now. " +4 days"));	break;	case 4://thursday		$start = date('Y-m-d', strtotime($now. " -3 days"));		$end = date('Y-m-d', strtotime($now. " +3 days"));	break;	case 5://friday			$start = date('Y-m-d', strtotime($now. " -4 days"));		$end = date('Y-m-d', strtotime($now. " +2 days"));	break;	case 6://saturday		$start = date('Y-m-d', strtotime($now. " -5 days"));		$end = date('Y-m-d', strtotime($now. " +1 days"));	break;}	$ukstart = date('d/m/Y',strtotime($start));$ukend = date('d/m/Y',strtotime($end));?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><!-- InstanceBegin template="/Templates/search_results.dwt" codeOutsideHTMLIsLocked="false" --><head><title><?echo $name;?>&nbsp;Ingredients</title><meta name="HandheldFriendly" content="true" /><meta name="MobileOptimized" content="320" /><meta name="viewport" content="initial-scale=1.0, maximum-scale=1.0, user-scaleable=no, width=device-width" /><link rel="stylesheet" href="storytapes.css"><link rel="shortcut icon" href="http:/